var connector = require('./index.js'),
	assert = require('assert');

connector.getProjects()
.then(function(results){
	assert.equal(typeof results[0].url, "string");
	assert.equal(typeof results[0].title, "string");

	console.log("test nd-connector OK");
})
.catch(log);

function log(e){
	console.log(e.stack);
}

